DROP TABLE IF EXISTS `view_alarm_code`;
CREATE ALGORITHM=UNDEFINED DEFINER=`sanwa`@`localhost` SQL SECURITY DEFINER VIEW `view_alarm_code` AS WITH T1 AS (SELECT a.*
 FROM system_code_error a
WHERE (a.node_type = 'ALL' 
  AND a.vendor = 'ALL') OR
  (a.node_type = 'SYSTEM' 
  AND a.vendor = 'SANWA')
 ),
 T2 AS (SELECT DISTINCT a.equipment_model_id
             FROM config_node a
          )
          
SELECT f.equipment_model_id, d.node_type, d.vendor, d.node_id,
       CONCAT(d.code_id,e.code_id) AS sys_code_id,
 	 d.code_id AS device_code_id,
 	 CONVERT(CONVERT(substring(d.code_id, 3, 2), INT) USING UTF8) AS device_address_id,
 	 e.code_id AS error_code_id,
 	 e.model_no, e.category, e.return_code_type, e.return_code, e.code_name,
 e.code_desc, e.code_desc_en, e.is_stop , e.active,
 CASE WHEN is_complex_location = 'N' THEN e.location
      WHEN TRIM(COALESCE(d.code_id,'')) = '' THEN e.location
      WHEN TRIM(COALESCE(e.code_id,'')) = '' THEN d.location
 	 ELSE CONCAT_WS('_', d.location,e.location) END AS location
  FROM system_code_device d
  LEFT JOIN system_code_error e
    ON d.node_type = e.node_type
   AND d.vendor = e.vendor
 INNER JOIN config_node f 
    ON f.node_id = d.node_id
   AND f.vendor = d.vendor
UNION ALL
SELECT  DISTINCT f.equipment_model_id, 'SYSTEM' node_type, 'SANWA' vendor, d.node_id,
       CONCAT(d.code_id,e.code_id) AS sys_code_id,
 	 d.code_id AS device_code_id,
 	 CONVERT(CONVERT(substring(d.code_id, 3, 2), INT) USING UTF8) AS device_address_id,
 	 e.code_id AS error_code_id,
 	 e.model_no, e.category, e.return_code_type, e.return_code, e.code_name,
 e.code_desc, e.code_desc_en, e.is_stop , e.active,
 CASE WHEN is_complex_location = 'N' THEN e.location
      WHEN TRIM(COALESCE(d.code_id,'')) = '' THEN e.location
      WHEN TRIM(COALESCE(e.code_id,'')) = '' THEN d.location
 	 ELSE CONCAT_WS('_', d.location,e.location) END AS location
  FROM system_code_device d
  LEFT JOIN T1 e ON 1 = 1
 INNER JOIN T2 f ON 1 = 1
 WHERE d.node_id = 'System'
ORDER BY device_code_id ;

