     SELECT DATE_FORMAT(NOW(),'p_%Y_%m_11') AS p_name, DATE_FORMAT(NOW(),'%Y-%m-11') AS max_date
      UNION ALL
     SELECT DATE_FORMAT(NOW(),'p_%Y_%m_21') AS p_name, DATE_FORMAT(NOW(),'%Y-%m-21') AS max_date
	), t_tmp AS(
	  SELECT DISTINCT table_name AS t_name
       FROM view_partitions
      WHERE partition_name IS NOT NULL  
	), l_tmp AS(
	SELECT *
	  FROM p_tmp, t_tmp 
	)
	SELECT *
     FROM l_tmp l 
	 WHERE NOT EXISTS (SELECT 'X' 
	                     FROM INFORMATION_SCHEMA.PARTITIONS p
 							  WHERE table_schema = 'adam'
								 AND l.t_name = p.table_name
								 AND l.p_name = p.partition_name ) ;

